bool Access::validateJsonForCreation(const Json::Value &pJson, std::string &err)
{
    if(pJson.isMember("id"))
    {
        if(!validJsonOfField(0, "id", pJson["id"], err, true))
            return false;
    }
    else
    {
        err="The id column cannot be null";
        return false;
    }
    if(pJson.isMember("users_id"))
    {
        if(!validJsonOfField(1, "users_id", pJson["users_id"], err, true))
            return false;
    }
    else
    {
        err="The users_id column cannot be null";
        return false;
    }
    if(pJson.isMember("devices_id"))
    {
        if(!validJsonOfField(2, "devices_id", pJson["devices_id"], err, true))
            return false;
    }
    else
    {
        err="The devices_id column cannot be null";
        return false;
    }
    if(pJson.isMember("token"))
    {
        if(!validJsonOfField(3, "token", pJson["token"], err, true))
            return false;
    }
    else
    {
        err="The token column cannot be null";
        return false;
    }
    if(pJson.isMember("created_at"))
    {
        if(!validJsonOfField(4, "created_at", pJson["created_at"], err, true))
            return false;
    }
    else
    {
        err="The created_at column cannot be null";
        return false;
    }
    if(pJson.isMember("deleted_at"))
    {
        if(!validJsonOfField(5, "deleted_at", pJson["deleted_at"], err, true))
            return false;
    }
    else
    {
        err="The deleted_at column cannot be null";
        return false;
    }
    return true;
}
